			// 				grant : 0,
			// 				newTab : false,
			// 				subItems : []
			// 			}
			// 		]
			// 	},
			// 	{
			// 		title : "About",
			// 		icon : "mdi-help",
			// 		to : "/about",
			// 		grant : 0,
			// 		newTab : false,
			// 		subItems : []
			// 	}
			// ],
		},
		initFetchs: [],
		initData : null,
	},
	mutations: {
		SET_APP_READY(state) {
			state.appReady = true;
		},
		SET_CONFIG(state, { key, value }) {

			try {
				value = JSON.parse(value);
			} catch (e) { }

			if (state.config[key]) {
				state.config[key] = value;
			} else {
				Vue.set(state.config, key, value);
			}
		},
		PUSH_FETCH(state, tag){
			state.initFetchs.push(tag);
		},
		SET_INITDATA(state, data){
			if(data == null){
				this.initFetchs = null;
				this.initData = null;
			}else{
				const keys = Object.keys(data);
				if(state.initData == null){
					state.initData = {};
				}
				for(const key of keys){
					state.initData[key] = data[key];
				}
			}
		}
	},
	getters: {
		access(state) {
			const obj = {};
			if (state.config.menu) {
				menuAccess(obj, state.config.menu);
			}
			//console.log(obj);
			return obj;
		}
	},
	actions: {
		async appInit({ dispatch, commit }, ctx) {
			// site 설정을 가지고 올꺼고
			if (ctx) {
				const keys = Object.keys(ctx.config);
				//console.log('appInit keys : ',keys)
				for (const key of keys) {
					commit('SET_CONFIG', { key, value: ctx.config[key] });
				}

				commit('user/SET_MEMBER', ctx.member);
				commit('user/SET_TOKEN', ctx.token);
				if (ctx.member) {
					commit('socket/ROOM_JOIN', ctx.member.mb_id);
				}
			} else {
				await dispatch('configLoad');
				await dispatch('user/initUser');
			}
			commit('SET_APP_READY');
		},
		async configDuplicateCheck(ctx, { field, value }) {
			const { $axios } = Vue.prototype;
			const data = await $axios.get(`/api/config/duplicateCheck/${field}/${value}`);
			//console.log('data',data );
			return data;
		},
		async configSave(ctx, form) {
			const { $axios } = Vue.prototype;
			const data = await $axios.post('/api/config', form);
			//console.log('data',data );
			return data;
		},
		async configLoad({ commit }) {
			const { $axios } = Vue.prototype;
			const data = await $axios.get('/api/config');
			//console.log('data : ', data);
			const keys = Object.keys(data);

			for (const key of keys) {
				commit('SET_CONFIG', { key, value: data[key] });
			}
		}
	},
	modules
});

export function createStore() {

	return store;
}

export default store;

import Vue from 'vue'
import VueRouter from 'vue-router'
import routes from './routes';
import store from '../store';
import { LV, LV_LABEL } from '../../util/level';

Vue.use(VueRouter)

export function createRouter() {
  const router = new VueRouter({
    mode: 'history',
    base: process.env.BASE_URL,
    routes
  });

  router.beforeEach(async (to, from, next) => {
    // console.log('router.js beforeEach');
    const { $Progress, $toast } = Vue.prototype;

    if ($Progress) $Progress.start();

    if (typeof (window) == 'object') {
      if (!store.state.appReady) {
        if (window.__INITIAL_STATE__) {
          store.replaceState(window.__INITIAL_STATE__);
        } else {

          await store.dispatch('appInit');
        }
      }
    }

    const access = store.getters.access;
    const GRANT = store.getters['user/GRANT'];
    const isMember = !!store.state.user.member;
    //console.log("index.js===>", access, GRANT, isMember);
    //console.log("name===>", to.name);

    let msg = '';
    if (to.name.startsWith('NoAuth') && isMember) {
      // 비회원 인경우에만 접근
      msg = "이미 로그인 되어 있습니다.";
    } else if (to.name.startsWith('Adm') && GRANT < LV.ADMIN) {
      // 관리자 전용 페이지
      msg = `${LV_LABEL(LV.ADMIN)} 이상 접근 가능합니다.`;
    } else {
      // 메뉴 접근 레벨에 따름
      const accessLV = access[to.path] || LV.BLOCK;
      //console.log("accessLV===>", accessLV);
      if (accessLV > GRANT) {
        msg = `${LV_LABEL(accessLV)} 이상 접근 가능합니다.`;
      }
    }
    if (msg) {
      // 접근차단
      if ($toast) $toast.error(msg);
      if ($Progress) $Progress.fail();
      if (from.name) { // 이전 경로가 있으면 라우팅을 동작하지 않고
        return isMember ? next('/') : next(false);
      } else { // 이전 경로가 없으면 홈으로 이동 또는 로그인 페이지로 보내도 됨
        return isMember ? next('/') : next({
          name: 'NoAuthLogin',
          query: { next: to.fullPath }
        });
      }
    } else {
      // 통과
      if ($Progress) $Progress.finish();
      next();
    }


  });

  router.afterEach((to, from) => {
    //console.log('router.js afterEach');
  });

  return router;
}

import Vue from 'vue'
import App from './App.vue'
import {createRouter} from './router'
import {createStore} from './store'
import {sync} from 'vuex-router-sync'

import plugins from './plugins';
//console.log('main.js plugins===>',plugins);
import '@babel/polyfill'
import 'roboto-fontface/css/roboto/roboto-fontface.css'
import '@mdi/font/css/materialdesignicons.css'

import Mixins from './mixins';

Vue.config.productionTip = false

export function createApp(ctx){
  //console.log('main.js createApp');
  const router = createRouter();
  const store = createStore();
  sync(store, router);

  const mixins = Object.keys(Mixins);
  for(const mixin of mixins){
    Vue.mixin(Mixins[mixin]);
  }

  const app = new Vue({
    date : {url:ctx ? ctx.url : ''},
    router,
    store,
    vuetify : plugins.vuetify,
    render: h => h(App)
  });
  return {
    app, router, store

  }
}

import { createApp } from './main';

export default (ctx) => {
	return new Promise(async (resolve, reject) => {
		const { app, router, store } = createApp(ctx);
		//console.log('entry-server.js before router.push');
		//console.log('entry-server.js =================>',ctx);
		await store.dispatch('appInit', ctx);

		router.push(ctx.url);
		//console.log('entry-server.js after router.push');
		router.onReady(() => {
			//console.log('entry-server.js onReady');
			ctx.rendered = () => ctx.state = store.state;
			const matchedComponents = router.getMatchedComponents();
			if (matchedComponents.length === 0) {
				return reject({ code: 404 });
			}
			return resolve(app);
		}, reject);
	});
}

